mapper.select:
select a.name ,jg.app_id ,jg.bizDate ,jg.task_count ,jg.status_not_running ,jg.status_waiting ,jg.status_waiting_resource ,jg.status_running ,jg.status_failed ,jg.status_succeed ,jg.task_begin ,jg.task_finished ,jg.task_finished-jg.task_begin as task_working_time from phoenix_app_config a left outer join ( select task_inst.app_id ,task_inst.bizdate ,COUNT(task_inst.app_id) as task_count ,sum(case when task_inst.status=1 then 1 else 0 end) as status_not_running ,sum(case when task_inst.status=2 then 1 else 0 end) as status_waiting ,sum(case when task_inst.status=3 then 1 else 0 end) as status_waiting_resource ,sum(case when task_inst.status=4 then 1 else 0 end) as status_running ,sum(case when task_inst.status=5 then 1 else 0 end) as status_failed ,sum(case when task_inst.status=6 then 1 else 0 end) as status_succeed ,min(begin_run_time) as task_begin ,max(finish_time) as task_finished from phoenix_task_inst task_inst
task_inst.task_type in ('0','5')
and gmtdate = #{gmtDate}
group by app_id) jg on a.app_id=jg.app_id where upper(a.name) like 'SC_J%T' or upper(a.name) like 'SC_J%_GSZJ' order by a.name;
